;(function($){

    let $range = $("#range");
    let $inputFrom = $("#rangeFrom");
    let $inputTo = $("#rangeTo");
    let instance;
    let min = 0;
    let max = 1000;
    let from = 0;
    let to = 0;

    $(".js-range-slider").ionRangeSlider({
        type:'double',
        min:0,
        max:1000,
        from:200,
        to:500,
        prefix: "$",
        onStart: updateInputs,
        onChange: updateInputs,
        onFinish: updateInputs

    });
    instance = $range.data("ionRangeSlider");

    function updateInputs (data) {
        from = data.from;
        to = data.to;

        $inputFrom.prop("value", from);
        $inputTo.prop("value", to);
    }

    $inputFrom.on("change", function () {
        var val = $(this).prop("value");

        // validate
        if (val < min) {
            val = min;
        } else if (val > to) {
            val = to;
        }

        instance.update({
            from: val
        });

        $(this).prop("value", val);

    });

    $inputTo.on("change", function () {
        var val = $(this).prop("value");

        // validate
        if (val < from) {
            val = from;
        } else if (val > max) {
            val = max;
        }

        instance.update({
            to: val
        });

        $(this).prop("value", val);
    });

    $('.btn').on('click', function(){
        $('.aside').slideToggle(300);
        $('.products-img-block').css('opacity', '0');
        $('.news-left').css('opacity', '0');
        $('.more').css('opacity', '0');
    })

    $('.mob-menu').on('click', function(){

        $('.link-list').slideToggle(300);
        $('body').css('overflow-y', 'hidden');
        
    })
    $('.cross').on('click', function(){
        $('.link-list').slideToggle();
        $('body').css('overflow-y', 'scroll');

    })


    $('.cross2').on('click', function(){
        $('.aside').slideToggle();
        $('.products-img-block').css('opacity', '1');
        $('.news-left').css('opacity', '1');
        $('.more').css('opacity', '1');
    })



})(jQuery);
